mapper.update:
UPDATE 
	answer 
SET 
	answer_content = #{answer_content} 
WHERE
	question_no = #{question_no}

mapper.insert:
INSERT INTO answer(
	question_no
	, trainer_no
	, answer_title
	, answer_content
	, answer_date
)
VALUES (
	#{question_no}
	, #{trainer_no}
	, #{answer_title}
	, #{answer_content}
	, NOW()
)
